function sortPerkArrayByLevel() {
    character.perkArray.sort(function(a, b) {
        var sortA = 0;
        var sortB = 0;

        if (a.levels != b.levels) {
            return (a.levels - b.levels);
        }

        if (a.priority != b.priority) {
            return (a.priority - b.priority);
        }
        switch (a.special.substring(0, 1)) {
            case "s":
                sortA = 1;
                break;
            case "p":
                sortA = 2;
                break;
            case "e":
                sortA = 3;
                break;
            case "c":
                sortA = 4;
                break;
            case "i":
                sortA = 5;
                break;
            case "a":
                sortA = 6;
                break;
            case "l":
                sortA = 7;
                break;
        }
        switch (b.special.substring(0, 1)) {
            case "s":
                sortB = 1;
                break;
            case "p":
                sortB = 2;
                break;
            case "e":
                sortB = 3;
                break;
            case "c":
                sortB = 4;
                break;
            case "i":
                sortB = 5;
                break;
            case "a":
                sortB = 6;
                break;
            case "l":
                sortB = 7;
                break;
        }
        if (sortA == sortB) {
            return (a.rank - b.rank);
        }
        return (sortA - sortB);
    })
}

